import React, { useState, useEffect } from 'react';
import { StyleSheet, View, TextInput } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import * as ImagePicker from 'expo-image-picker';
import AsyncStorage from '@react-native-async-storage/async-storage';

import Button from './Button';
import ImageViewer from './ImageViewer';

const PlaceholderImage = require('../assets/images/background-image.png');

const ImagePickerScreen = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [description, setDescription] = useState('');

  useEffect(() => {
    retrieveData();
  }, []);

  const retrieveData = async () => {
    try {
      const imageUri = await AsyncStorage.getItem('selectedImage');
      const desc = await AsyncStorage.getItem('description');

      if (imageUri !== null) {
        setSelectedImage(imageUri);
      }

      if (desc !== null) {
        setDescription(desc);
      }

    } catch (error) {
      console.log('Failed to retrieve data from storage:', error);
    }
  };

  const saveData = async (uri, desc) => {
    try {
      await AsyncStorage.setItem('selectedImage', uri);
      await AsyncStorage.setItem('description', desc);
    } catch (error) {
      console.log('Failed to save data to storage:', error);
    }
  };

  const pickImageAsync = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      allowsEditing: true,
      quality: 1,
    });

    if (!result.cancelled) {
      setSelectedImage(result.uri);
      saveData(result.uri, description);
    } else {
      alert('You did not select any image.');
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.imageContainer}>
        <ImageViewer
          placeholderImageSource={PlaceholderImage}
          selectedImage={selectedImage}
        />
      </View>
      <TextInput 
        style={styles.input}
        onChangeText={setDescription}
        value={description}
        placeholder='Enter description'
      />
      <View style={styles.footerContainer}>
        <Button theme="primary" label="Choose a photo" onPress={pickImageAsync} />
        <Button label="Use this photo" />
      </View>
      <StatusBar style="auto" />
    </View>
  );
};

export default ImagePickerScreen;

const styles = StyleSheet.create({
  footerContainer: {
    flex: 1 / 3,
    alignItems: 'center',
  },
  container: {
    flex: 1,
    backgroundColor: '#25292e',
    alignItems: 'center',
  },
  imageContainer: {
    flex: 1,
    paddingTop: 58,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    color: 'white',
    borderColor: 'white',
  },
});
